class HashMap:
    def __init__(self, size=1000):
        self.size = size
        self.buckets = [[] for _ in range(size)]

    def _hash(self, key):
        return hash(key) % self.size

    def put(self, key, value):
        index = self._hash(key)
        bucket = self.buckets[index]
        for i, (k, v) in enumerate(bucket):
            if k == key:
                bucket[i] = (k, value)
                return
        bucket.append((key, value))

    def get(self, key):
        index = self._hash(key)
        bucket = self.buckets[index]
        for k, v in bucket:
            if k == key:
                return v
        return None

    def remove(self, key):
        index = self._hash(key)
        bucket = self.buckets[index]
        for i, (k, v) in enumerate(bucket):
            if k == key:
                del bucket[i]
                return

    def keys(self):
        keys = []
        for bucket in self.buckets:
            for k, v in bucket:
                keys.append(k)
        return keys

    def values(self):
        values = []
        for bucket in self.buckets:
            for k, v in bucket:
                values.append(v)
        return values

    def items(self):
        items = []
        for bucket in self.buckets:
            items.extend(bucket)
        return items

    def __str__(self):
        return str(self.items())

